
package projetc1;

public class Rectangle {

	public static void main(String[] args) {
		// TODO Auto-generated method
		int n=5;
		int i,j;
		for( i=1;i<=n;i++) {
			for( j=1;j<=n-i;j++) 
				System.out.print(" ");
				for( j=1;j<=i;j++) 
					System.out.print("* ");
					System.out.println();
				
					
		}

	}

}
