package projetc1;

public class class1 {
	public void eat() {
		System.out.println("I am eating");
	}
	
	public void run() {
		System.out.println("I am running");
	}
	
	public void book() {
		System.out.println("I am fond of reading books");
	}
	
	public void java() {
		System.out.println("Java is the best coding language of the Era");
	}
	
	public void hobby() {
		System.out.println("My hobby is Singing");
	}
	
	public void fun() {
		System.out.println("I have fun coding");
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		class1 obj = new class1();
		obj.eat();
		obj.run();
		obj.book();
		obj.java();
		obj.hobby();
		obj.fun();
	}

}
